mapper.select:
select id, fullname, appellation, company, phone, createBy, mphone, state, source from tbl_clue
fullname like '%' #{fullname} '%'
and company like '%' #{company} '%'
and phone like '%' #{phone} '%'
and source = #{source}
and createBy like '%' #{clueOwner} '%'
and mphone like '%' #{mphone} '%'
and state = #{state}
order by createTime desc limit #{skipCount},#{pageSize}
